mapper.select:
   select *
    from(
      select A.*, rownum as rn
      from ( 
        select num,writer,subject,reg_date,readcount
				,filename,filesize,down,re_level
				,(select count(*) from culture_comment
				where culture_num=num) comment_count, show
				from culture
				order by ref desc, re_step
        ) A
     )
     where rn between #{start} and #{end}

select filename
from culture
where num=#{num}

mapper.update:
update culture
set re_step=re_step+1
where ref=#{ref} and re_step >= #{re_step}

  update culture
	set writer=#{writer}, subject=#{subject}
	 , content=#{content}, filename=#{filename}
	 , filesize=#{filesize}, down=#{down}
	where num=#{num}
